package org.sandwood.compiler.tests.parser;

model HMMTestPart3d(boolean[] flipsMeasured) {
        int states = 2;

        double[] v = new double[states];
        for(int i:[0..states))
            v[i] = 0.1;

        double[][] m = dirichlet(v).sample(states);
        double[] bias = beta(1.0, 1.0).sample(states);

        int samples = flipsMeasured.length;
        int[] st = new int[samples];
        int[] st2 = new int[samples];

        st[0] = categorical(m[0]).sample();
        st2[0] = samples - st[0];

        for(int i:[1..samples)) {
            st[i] = categorical(m[samples - st2[i-1]]).sample();

            int[] indirection = new int[i+1];
            for(int k:[0..i])
                indirection[k] = k*i; 

            st2[indirection[i]/i] = samples - st[indirection[i]/i];
        }

        boolean[] flips = new boolean[samples];

        for(int j:[0..samples))
            flips[j] = bernoulli(bias[samples - st2[j]]).sample();

        flips.observe(flipsMeasured);
}
